instancemethods.updateUrl:
function (newUrl, cb) {
      this.url = newUrl;
      this.save(function(e, d){cb(e,d);});
    }

staticmethods.randomNFromFlickr:
function (searchQ, limit, cb) {
      var flickr = require("flickr");
      var fcli = new flickr.Flickr('cbe3f7f6cbf9d13ad243a1e1afec902d', '5c3f9a33e088ef60');
      fcli.executeAPIRequest('flickr.photos.search', {text: searchQ, per_page: limit},
                               false, function(e, d){cb(e, d);});
    }
